import React from 'react';
import {useEffect}  from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getDetail } from '../reducer/actions';
import { Link, Redirect } from 'react-router-dom';



function RecipeDetail(props){
    console.log(props)
    const dispatch= useDispatch()

    useEffect(() => {
        dispatch(getDetail(props.match.params.id));
    },[dispatch])

    const recipeId = useSelector((state) => state.recipeDetail)
    console.log(recipeId)
    return(
        <div>
            
            {recipeId?
                <div>
                    <h1>Name: {recipeId[0].name}</h1>
                    <img src={recipeId[0].image?recipeId[0].image:recipeId[0].image} alt=" not found" />
                    <h2>Summary: {recipeId[0].summary}</h2>
                    <h2>Health Score: {recipeId[0].healthScore}</h2>
                    <h3>{!recipeId[0].id.includes('-')? recipeId[0].diets : recipeId[0].diets.map(e => e.name + (' '))}</h3>
                
                </div> : <p>Loading...</p>
            }
            <Link to='/home'>
                <button>Back</button>
            </Link>
        </div>
    )
};


export default RecipeDetail
