import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { getDiets, postRecipe } from "../reducer/actions";



export default function CreateRecipe(){
    const dispatch = useDispatch();
    const diets = useSelector((state) => state.diets);
    const lis = listDiets.map((e) => e.name);

    const[input,setInput] = useState({
        name: '',
        summary: '',
        image:'',
        healthScore: '',
        steps:'',
        diets:[]
    });


    useEffect(() => {
        dispatch(getDiets());
    },[]);

    return (
        <div>
            <h1>Create your recipe</h1>
            <form>
                <div>
                    <label>Name:</label>
                    <input type="text" value={input.name} name='name'/>
                </div>
                <div>
                    <label>Summary</label>
                    <input type="text" value={input.summary} name='summary'/>
                </div>
                <div>
                    <label>Image</label>
                    <input type="text" value={input.image} name='image'/>
                </div>
                <div>
                    <label>Health Score</label>
                    <input type="text" value={input.healthScore} name='healthScore'/>
                </div>
                <div>
                    <label>Health Score</label>
                    <input type="text" value={input.healthScore} name='healthScore'/>
                </div>
                <div>
                    <label>Steps</label>
                    <input type="text" value={input.healthScore} name='healthScore'/>
                </div>
                
                {/* DIETS CONTAINER*/}
                <div >
              <label>Diets: </label>
              <select name="diets">
                {lis.map((diet) => (
                  <option key={diet} value={diet}>
                    {diet[0].toUpperCase() + diet.substring(1)}
                  </option>
                ))}
              </select>
              <div >
                {form.diets.map((d) => (
                  <div key={d} >
                    <p>{d[0].toUpperCase() + d.substring(1)}</p>
                    <button>
                      X
                    </button>
                  </div>
                ))}
              </div>
            </div>
                




            </form>
        </div>
    )








}
