import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { getDiets, postRecipe } from "../reducer/actions";



export default function CreateRecipe() {
    const dispatch = useDispatch();
    const diets = useSelector((state) => state.diets);
    const lis = diets.map((e) => e.name);

    const [form, setForm] = useState({
        name: '',
        summary: '',
        image: '',
        healthScore: '',
        steps: '',
        diets: []
    });

    function handleChange(e) {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        })
        console.log(form)
    };

    /*     function handleCheck(e) {
            if (e.target.checked) {
                setForm({
                    ...form,
                    diets: [...form.diets, e.target.value]
                })
            }
            console.log(form)
        }; */

    function handleSelect(e) {
        setForm({
            ...form,
            diets: [...form.diets, e.target.value]
        })
    }
    



    function handleSubmit(e) {
        e.preventDefault(e);
        console.log(form);
        dispatch(postRecipe(form));
        alert('¡Receta creada!');
        setForm({
            name: '',
            summary: '',
            image: '',
            healthScore: '',
            steps: '',
            diets: []
        })

    }



    useEffect(() => {
        dispatch(getDiets());
    }, []);




    return (
        <div>
            <h1>Create your recipe</h1>
            <form onSubmit={(e) => handleSubmit(e)}>
                <div>
                    <label>Name:</label>
                    <input type="text" value={form.name} name='name' onChange={(e) => handleChange(e)} />
                </div>
                <div>
                    <label>Summary</label>
                    <input type="text" value={form.summary} name='summary' onChange={(e) => handleChange(e)} />
                </div>
                <div>
                    <label>Image</label>
                    <input type="text" value={form.image} name='image' onChange={(e) => handleChange(e)} />
                </div>
                <div>
                    <label>Health Score</label>
                    <input type="text" value={form.healthScore} name='healthScore' onChange={(e) => handleChange(e)} />
                </div>
                <div>
                    <label>Steps</label>
                    <input type="text" value={form.steps} name='steps' onChange={(e) => handleChange(e)} />
                </div>
                <div>
                    <label>Diets</label>

                    {/*                 {
                    diets.map((e) => {
                        return(
                        <div>
                            <label>
                                <input type="checkbox" name={e.name} value={e.name} onChange={(e) =>handleCheck(e)}/>
                                {e.name[0].toUpperCase() + e.name.substring(1)}
                            </label>
                        </div>
                        )
                    })
                } */}
                    <select onChange={(e) => handleSelect(e)}>
                        <option value='all'>All</option>
                        {
                            diets.map((e) => {
                                return (
                                    <option value={e.name}>{e.name[0].toUpperCase() + e.name.substring(1)} key:{e.id}</option>
                                )
                            })
                        }
                    </select>
                    <ul><li>{form.diets.map(e => e[0].toUpperCase() + e.substring(1) + ', ')}</li></ul>
                </div>

                <button type="submit">Crear receta</button>
            </form>
        </div>
    )








}
