import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { getDiets, postRecipe } from "../reducer/actions";



export default function CreateRecipe() {
    const dispatch = useDispatch();
    const diets = useSelector((state) => state.diets);
    const lis = diets.map((e) => e.name);

    const [form, setForm] = useState({
        name: '',
        summary: '',
        image: '',
        healthScore: '',
        steps: '',
        diets: []
    });


    useEffect(() => {
        dispatch(getDiets());
    }, []);

    return (
        <div>
            <h1>Create your recipe</h1>
            <form>
                <div>
                    <label>Name:</label>
                    <input type="text" value={form.name} name='name' />
                </div>
                <div>
                    <label>Summary</label>
                    <input type="text" value={form.summary} name='summary' />
                </div>
                <div>
                    <label>Image</label>
                    <input type="text" value={form.image} name='image' />
                </div>
                <div>
                    <label>Health Score</label>
                    <input type="text" value={form.healthScore} name='healthScore' />
                </div>
                <div>
                    <label>Steps</label>
                    <input type="text" value={form.healthScore} name='healthScore' />
                </div>
                <div>
                    <label>Diets</label>
                </div>
                {
                    diets.map((e) => {
                        return(<div>
                            <label>
                                <input type="checkbox" name={e.name[0].toUpperCase() + e.name.substring(1)} value={e.name} />
                                {e.name}
                            </label>
                        </div>)
                    })
                }
            </form>
        </div>
    )







}
