import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { getDiets, postRecipe } from "../reducer/actions";



export default function CreateRecipe() {
    const dispatch = useDispatch();
    const diets = useSelector((state) => state.diets);
    const lis = diets.map((e) => e.name);

    const [form, setForm] = useState({
        name: '',
        summary: '',
        image: '',
        healthScore: '',
        steps: '',
        diets: []
    });

    function handleChange(e){
        setForm({
            ...form,
            [e.target.name] : e.target.value
        })
    };

    function handleCheck(e){
        if(e.target.checked){
            setForm({
                ...form,
                diets: diets.push(e.target.value)
            })
        }
    }



    useEffect(() => {
        dispatch(getDiets());
    }, []);




    return (
        <div>
            <h1>Create your recipe</h1>
            <form>
                <div>
                    <label>Name:</label>
                    <input type="text" value={form.name} name='name' onChange={handleChange}/>
                </div>
                <div>
                    <label>Summary</label>
                    <input type="text" value={form.summary} name='summary' onChange={handleChange}/>
                </div>
                <div>
                    <label>Image</label>
                    <input type="text" value={form.image} name='image' onChange={handleChange} />
                </div>
                <div>
                    <label>Health Score</label>
                    <input type="text" value={form.healthScore} name='healthScore' onChange={handleChange}/>
                </div>
                <div>
                    <label>Steps</label>
                    <input type="text" value={form.healthScore} name='healthScore' onChange={handleChange}/>
                </div>
                <div>
                    <label>Diets</label>
                
                {
                    diets.map((e) => {
                        return(
                        <div>
                            <label>
                                <input type="checkbox" name={e.name} value={e.name} />
                                {e.name[0].toUpperCase() + e.name.substring(1)}
                            </label>
                        </div>
                        )
                    })
                }
                </div>
            </form>
        </div>
    )








}
