import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { getDiets, getRecipes, postRecipe } from "../../reducer/actions";
import Banner from "../Banner/Banner";
import NavBar from "../NavBar/NavBar"
import s from './CreateRecipe.module.css'




export default function CreateRecipe() {
    const dispatch = useDispatch();
    const diets = useSelector((state) => state.diets);
    const [errors, setErrors] = useState({})
    const allRecipes = useSelector((state) => state.recipes);

    const [form, setForm] = useState({
        name: '',
        summary: '',
        image: '',
        healthScore: '',
        steps: '',
        diets: []
    });


    useEffect(() => {
        dispatch(getDiets())
    }, [dispatch]);



    function validate(form) {
        let errors = {};
        if (!form.name) errors.name = 'Name of recipe is required';
        if (form.name && !/^[a-zA-Z" "]*$/.test(form.name)) errors.name = 'The name cannot contain numbers or special characters';
        if (allRecipes.find(e => e.name === form.name)) errors.name = ('We already have a recipe with that name')
        if (!form.summary) errors.summary = 'Summary of recipe is required';
        if (form.healthScore < 0 || form.healthScore > 100) errors.healthScore = ('The Health Score must be between 0 and 100');
        if (isNaN(form.healthScore)) errors.healthScore = ('The Health Score must be a number');

        return errors
    };

    function handleChange(e) {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        })
        setErrors(validate({
            ...form,
            [e.target.name]: e.target.value
        }))

    };

    function handleSelect(e) {
        setForm({
            ...form,
            diets: [...form.diets, e.target.value]
        })

    }


    function handleSubmit(e) {
        e.preventDefault(e);
        dispatch(postRecipe(form));
        setForm({
            name: '',
            summary: '',
            image: '',
            healthScore: '',
            steps: '',
            diets: []
        })

        alert('The recipe was created successfully')

    };

    function handleReset(e) {
        setForm({
            ...form,
            diets: []
        })
    }


    return (

        <div>
            {console.log(diets)}
            <NavBar />
            <Banner />

            <h1>Create your recipe</h1>

            
            <form className={s.formcontainer} onSubmit={(e) => handleSubmit(e)}>
                <div>
                    <label>Name:</label>
                    <input type="text" value={form.name} name='name' onChange={(e) => handleChange(e)}/>
                </div>
                <div>
                    <label className={s.title}>Summary:</label>
                    <textarea className={s.textarea} type="text" value={form.summary} name='summary' onChange={(e) => handleChange(e)}/>
                </div>
                <div>
                    <label>Image:</label>
                    <input type="text" value={form.image} name='image' onChange={(e) => handleChange(e)}/>
                </div>
                <div>
                    <label>Health Score:</label>
                    <input type="number" value={form.healthScore} name='healthScore' onChange={(e) => handleChange(e)}/>
                </div>
                <div>
                    <label className={s.title}>Steps:</label>
                    <textarea textarea className={s.textarea} type="text" value={form.steps} name='steps' onChange={(e) => handleChange(e)} />
                </div>
                <div>
                    <label>Diets:</label>

                    <select onChange={(e) => handleSelect(e)}>
                        {
                            diets.map((e) => {
                                return (
                                    <option value={e.name}>{e.name[0].toUpperCase() + e.name.substring(1)}</option>
                                )
                            })
                        }
                    </select>
                    <h5>{form.diets.map(e => e[0].toUpperCase() + e.substring(1) + ', ')}</h5>


                    <button type="button" onClick={handleReset}> Reset diets</button>
                </div>

                <button disabled={form.name === '' || errors.name || errors.summary || errors.healthScore}>
                    Submit
                </button>

                <div>
                    <h4>Errors:</h4>

                    <div className={s.errors}>
                        {errors.name && (<h4>{errors.name}</h4>)}
                        {errors.summary && (<h4>{errors.summary}</h4>)}
                        {errors.healthScore && (<h4>{errors.healthScore}</h4>)}
                    </div>
                </div>

            </form>
        </div>
    )
}
