import React, { Fragment } from 'react';
import {useState, useEffect}  from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getRecipes,filterRecipeByStatus } from '../reducer/actions';
import { Link } from 'react-router-dom';
import Card from './Card';



function Home() {

    const dispatch = useDispatch()
    const allRecipes = useSelector((state) => state.recipes)

    useEffect(() => {
        dispatch(getRecipes())
    }, [dispatch]);


    function handleClick(e) {
        e.preventDefault();
        dispatch(getRecipes())
    };

    function handleFilterStatus(e){
        dispatch(filterRecipeByStatus(e.target.value))
    };



// Falta input para buscar y paginado
    return (
        <div>
            <Link to='/recipes'> Crear receta</Link>
            <h1>Cooking recipes</h1>
            <button onClick={e => { handleClick(e) }}>
                Volver a cargar recetas
            </button>
            <div>
                <select>
                    <option value='all'>All</option>
                    <option value='gluten free'>Gluten free</option>
                    <option value='ketogenic'>Ketogenic</option>
                    <option value='vegetarian'>Vegetarian</option>
                    <option value='lacto vegetarian'>Lacto vegetarian</option>
                    <option value='ovo vegetarian'>Ovo vegetarian</option>
                    <option value='lacto ovo vegetarian'>Lacto ovo vegetarian</option>
                    <option value='vegan'>Vegan</option>
                    <option value='pescetarian'>Pescetarian</option>
                    <option value='paleo'>Paleo</option>
                    <option value='primal'>Primal</option>
                    <option value='low FODMAP'>Low FODMAP</option>
                    <option value='whole30'>Whole30</option>
                </select>
                <select>
                    <option value='asc'>Ascendente</option>
                    <option value='des'>Descendente</option>
                    <option value='healthScore'>Health Score</option>
                </select>
                {
                    allRecipes?.map(e => {
                        return(
                        <Card name={e.name} diets={e.diets} image= {e.image} key={e.id}/>
                        )
                    })
                }

            </div>
        </div>
    )

};



export default Home
